mapper.select:
select b.board_no, b.board_type, b.subject subject, b.content content, date_format(b.create_time, '%Y-%m-%d %H:%i') create_time, count(bl.like_no) like_count, b.user_no, u.nickname from board b left join board_like bl on b.board_no = bl.board_no join user u on b.user_no = u.user_no where b.board_type = #{boardType}
and subject like concat('%', #{word}, '%')
and ${key} = #{word}
group by b.board_no order by b.board_no desc limit #{start}, #{listsize}

mapper.update:
update board
set subject = #{subject}, content = #{content}
where board_no = #{boardNo}
